mapper.insert:
		insert into core_news(title
			
    			,content
    		
			
    			,type
    		
    		
    			,creater_id
    		
    		
    			,time
    		
    		
    			,modify_user_id
    		
    		
    			,modify_time
    		) 
		values(#{title}
			
    			,#{content}
    		
			
    			,#{type}
    		
    		
    			,#{creater_id}
    		
    		
    			,#{time}
    		
    		
    			,#{modify_user_id}
    		
    		
    			,#{modify_time}
    		)
    	
            SELECT LAST_INSERT_ID() AS id

mapper.select:
    select t.id,t.title,t.content,t.type,date_format(t.modify_time, '%Y-%m-%d %H:%i:%s') modify_time,t.modify_user_id,u.fullname creater_name from core_news t,core_user u where t.modify_user_id=u.id and t.type=0 and t.is_delete=0 
    	
			and t.title like CONCAT('%',#{title},'%') 
		
		
			and u.fullname like CONCAT('%',#{creater_name},'%') 
		
		
			and t.modify_time >= #{beginDate} 
		
		
			and t.modify_time <= #{endDate} 
		
    order by t.modify_time desc

    select t.id,t.title,t.content,t.type,date_format(t.modify_time, '%Y-%m-%d %H:%i:%s') modify_time,t.modify_user_id,u.fullname creater_name from core_news t,core_user u where t.modify_user_id=u.id and t.type=1 and t.is_delete=0 
   		
			and t.title like CONCAT('%',#{title},'%') 
		
		
			and u.fullname like CONCAT('%',#{creater_name},'%') 
		
		
			and t.modify_time >= #{beginDate} 
		
		
			and t.modify_time <= #{endDate} 
		
    order by t.modify_time desc

mapper.update:
update core_news set title = #{title} 
	
		,content = #{content}
	
	
		,modify_time = #{modify_time}
	
	
		,modify_user_id = #{modify_user_id}
	
	where id=#{id}
