mapper.insert:
insert into core_report(title,author,institution,time,address,create_time,creater_id,modify_time,modify_user_id
,introduction
,serial_number) values(#{title},#{author},#{institution},#{time},#{address},#{create_time},#{creater_id},#{modify_time},#{modify_user_id}
,#{introduction}
,(select if(max(r.serial_number) is NULL,0,max(r.serial_number) )+1 from core_report r where r.create_time>=#{beginDate} and r.create_time<#{endDate}))
SELECT LAST_INSERT_ID() AS id

mapper.select:
    select t.id,t.title,serial_number,date_format(t.time, '%Y-%m-%d %H:%i') time,t.author,t.institution,date_format(t.modify_time, '%Y-%m-%d %H:%i:%s') modify_time,t.modify_user_id,u.fullname creater_name from core_report t,core_user u where t.modify_user_id=u.id and t.is_delete=0 

			and t.title like CONCAT('%',#{title},'%') 


			and u.fullname like CONCAT('%',#{creater_name},'%') 


			and t.time >= #{beginDate} 


			and t.time <= #{endDate} 

    order by t.time desc

mapper.update:
update core_report set title = #{title} 

		,author = #{author}


		,institution = #{institution}


		,time = #{time}


		,address = #{address}


		,introduction = #{introduction}


		,modify_time = #{modify_time}


		,modify_user_id = #{modify_user_id}

	where id=#{id}
